import React, { Component } from 'react';
import gql from 'graphql-tag';
import { Query, useQuery } from 'react-apollo';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Loader from '../loader/Loader';

// This query  gets us the Users stored in the DB

const GET_USERS = gql`
  {
    users {
      name
      userName
      email
    }
  }
`;

const FETCH_USER = gql`
  {
    user {
      name
      userName
      email
    }
  }
`;

export default class Home extends Component {
  render() {
    let currentUSer = this.props.user; // get the user name currently logged

    const logOut = () => {
      this.props.setLogin(false); // Log user out
    };

    const { loading, error, data } = useQuery(FETCH_USER);
    if (loading) return <Loader />;
    if (error) return `Error! ${error.message}`;
    return (
      <Query query={GET_USERS}>
        {({ loading, error, data }) => {
          if (loading) return <Loader />;
          if (error) return `Error! ${error.message}`;

          return (
            <div style={{ margin: '10px' }}>
              <h3
                style={{ textAlign: 'center' }}
              >{`Welcome, $`}</h3>
              <h4 style={{ textAlign: 'left' }}>
                The following table contains the current data stored in the
                Primsa DB
              </h4>
              <div style={{ margin: '10px 0 0 0' }}>
                <Table size="small">
                  <TableHead>
                    <TableRow>
                      <TableCell className="bold">UserName</TableCell>
                      <TableCell className="bold" align="right">
                        Name
                      </TableCell>
                      <TableCell className="bold" align="right">
                        Email
                      </TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {data.user.map((el, i) => (
                      <TableRow key={i}>
                        <TableCell className="bold">{el.userName}</TableCell>
                        <TableCell align="right">{el.name}</TableCell>
                        <TableCell align="right">{el.email}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </div>
              <div style={{ margin: '10px' }}>
                <button onClick={logOut}>Log Out</button>
              </div>
            </div>
          );
        }}
      </Query>
    );
  }
}
