  const [user, setUser] = useState('');



  return (
    <Switch>
            <Route
              exact
              path="/"
              component={() => login ? <Home  user={user} setUser={setUser} setLogin={setLogin}/> : <Redirect to='/SignIn' /> }
            />
            <Route
              exact
              path="/SignIn"
              component={() => <SignIn  setLogin={setLogin} setUser={setUser} />}
            />
            <Route
              exact
              path="/SignUp"
              component={() => <SignUp   />}
            />
          </Switch>
  );
}

export default App;
